// 1. Lấy dữ liệu thô
const rawInput = $input.first().json.output || '';

// --- HÀM TIỆN ÍCH ---

/**
 * Hàm tiện ích: "Làm sạch" các ký tự HTML đặc biệt trong một chuỗi.
 * @param {string} text - Văn bản cần làm sạch.
 * @returns {string} - Văn bản đã được làm sạch.
 */
function escapeHtml(text) {
  if (!text) return '';
  return text
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#39;");
}

/**
 * Định dạng một chuỗi dưới dạng khối mã, sử dụng hàm escapeHtml.
 * @param {string} text - Văn bản cần định dạng.
 * @returns {string} - Chuỗi HTML đã được định dạng.
 */
function formatAsCode(text) {
  return `<pre><code>${escapeHtml(text)}</code></pre>`;
}

/**
 * Chia văn bản thành các chunk AN TOÀN cho Telegram.
 * @param {string} text - Văn bản cần chia.
 * @param {number} maxLength - Độ dài ký tự tối đa cho mỗi chunk. Mặc định là 4000 để an toàn với Telegram.
 * @returns {string[]} - Mảng các chunk.
 */
function getTextChunks(text, maxLength = 4000) {
    if (!text || text.trim().length === 0) {
        return [];
    }

    const chunks = [];
    let textToProcess = text;

    while (textToProcess.length > 0) {
        if (textToProcess.length <= maxLength) {
            chunks.push(textToProcess);
            break;
        }

        let chunkCandidate = textToProcess.slice(0, maxLength);
        let splitIndex = maxLength; 

        const breakPoints = ['\n\n', '\n', '. ', ' '];
        for (const breakPoint of breakPoints) {
            const lastBreak = chunkCandidate.lastIndexOf(breakPoint);
            if (lastBreak > maxLength * 0.7) {
                splitIndex = lastBreak;
                break;
            }
        }

        const chunk = textToProcess.slice(0, splitIndex);
        chunks.push(chunk);
        
        textToProcess = textToProcess.slice(splitIndex).trim();
    }

    return chunks.filter(chunk => chunk.trim().length > 0);
}


// --- LOGIC CHÍNH ---
const allChunks = [];

// TRƯỜNG HỢP 1: Xử lý file bài tập đặc biệt của bạn
// Nếu input bắt đầu bằng '%%%==============SA_', coi toàn bộ là một khối mã
if (rawInput.trim().startsWith('%%%========')) {
    const codeChunks = getTextChunks(rawInput);
    for (const chunk of codeChunks) {
        allChunks.push({ json: { output: formatAsCode(chunk) } });
    }
} 
// TRƯỜNG HỢP 2: Xử lý các loại văn bản hỗn hợp khác
else {
    const patterns = [
      '%%%.*?%%%[\\s\\S]*?\\begin{bt}[\\s\\S]*?\\end{bt}',
      '%%%.*?%%%[\\s\\S]*?\\begin{ex}[\\s\\S]*?\\end{ex}',
      '%%%.*?%%%[\\s\\S]*?\\begin{vd}[\\s\\S]*?\\end{vd}',
      '```[\\s\\S]*?```',
      '%%%.*?%%%',
      '\\begin{ex}[\\s\\S]*?\\end{ex}',
      '\\begin{bt}[\\s\\S]*?\\end{bt}',
      '\\begin{vd}[\\s\\S]*?\\end{vd}',
      '\\begin{lstlisting}[\\s\\S]*?\\end{lstlisting}',
      '\\begin{verbatim}[\\s\\S]*?\\end{verbatim}',
      '\\begin{code}[\\s\\S]*?\\end{code}',
      '\\$\\$[\\s\\S]*?\\$\\$',
      '<code>[\\s\\S]*?</code>',
      '<pre>[\\s\\S]*?</pre>',
      '`[^`\\n]+`',
    ];

    const specialBlocksRegex = new RegExp(`(${patterns.join('|')})`, 'g');
    let currentIndex = 0;
    let match;

    try {
      while ((match = specialBlocksRegex.exec(rawInput)) !== null) {
        const plainText = rawInput.substring(currentIndex, match.index);
        if (plainText && plainText.trim()) {
          const plainTextChunks = getTextChunks(plainText);
          for (const chunk of plainTextChunks) {
            allChunks.push({ json: { output: chunk } });
          }
        }

        const specialBlock = match[0];
        if (specialBlock && specialBlock.trim()) {
          const specialBlockChunks = getTextChunks(specialBlock);
          for (const chunk of specialBlockChunks) {
            allChunks.push({ json: { output: formatAsCode(chunk) } });
          }
        }
        currentIndex = specialBlocksRegex.lastIndex;
      }

      const remainingText = rawInput.substring(currentIndex);
      if (remainingText && remainingText.trim()) {
        const plainTextChunks = getTextChunks(remainingText);
        for (const chunk of plainTextChunks) {
          allChunks.push({ json: { output: chunk } });
        }
      }
    } catch (error) {
      console.error('Error processing with regex, using fallback:', error);
      const fallbackChunks = getTextChunks(rawInput);
      for (const chunk of fallbackChunks) {
        allChunks.push({ json: { output: chunk } });
      }
    }
}

if (allChunks.length === 0 && rawInput.trim()) {
    const fallbackChunks = getTextChunks(rawInput);
    for (const chunk of fallbackChunks) {
        allChunks.push({ json: { output: chunk } });
    }
}

return allChunks;

