// Lấy dữ liệu từ item đầu tiên đi vào node
const input = items[0].json;

// Lấy ra hai mảng data và mimetype
const dataArray = input.data;
const mimetypeArray = input.mimetype;

// Kiểm tra để đảm bảo dữ liệu đầu vào là mảng và có cùng độ dài
if (!Array.isArray(dataArray) || !Array.isArray(mimetypeArray) || dataArray.length !== mimetypeArray.length) {
  throw new Error("Dữ liệu đầu vào không hợp lệ: 'data' và 'mimetype' phải là hai mảng có cùng số lượng phần tử.");
}

// Dùng hàm map để lặp qua mảng data và tạo cấu trúc mới
// Với mỗi 'data' ở vị trí 'index', ta lấy 'mimetype' ở cùng vị trí 'index'
const parts = dataArray.map((data, index) => {
  return {
    inlineData: {
      mimeType: mimetypeArray[index],
      data: data
    }
  };
});

// Trả về một item mới chứa kết quả đã biến đổi
// Kết quả được đặt trong một key mới là 'multi_data'
return [
  {
    json: {
      multi_data: parts
    }
  }
];

// 1. Lấy dữ liệu thô
const rawInput = $input.first().json.output || '';

// --- HÀM TIỆN ÍCH ---

/**
 * Hàm tiện ích: "Vô hiệu hóa" các ký tự HTML đặc biệt trong một chuỗi.
 * Đây là cách làm an toàn để tránh lỗi phân tích cú pháp của Telegram ở chế độ HTML.
 * @param {string} text - Văn bản cần xử lý.
 * @returns {string} - Văn bản đã được "vô hiệu hóa" ký tự HTML.
 */
function escapeHtml(text) {
  if (!text) return '';
  return text
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#39;");
}

/**
 * Định dạng một chuỗi dưới dạng khối mã, sử dụng hàm escapeHtml.
 * @param {string} text - Văn bản cần định dạng.
 * @returns {string} - Chuỗi HTML đã được định dạng.
 */
function formatAsCode(text) {
  // Nội dung bên trong <code> cần được escape để hiển thị chính xác dưới dạng mã nguồn
  return `<pre><code>${escapeHtml(text)}</code></pre>`;
}

/**
 * Chia văn bản thành các chunk AN TOÀN cho Telegram.
 * @param {string} text - Văn bản cần chia.
 * @param {number} maxLength - Độ dài ký tự tối đa cho mỗi chunk. Mặc định là 4000 để an toàn với Telegram.
 * @returns {string[]} - Mảng các chunk.
 */
function getTextChunks(text, maxLength = 4000) {
    if (!text || text.trim().length === 0) {
        return [];
    }

    const chunks = [];
    let textToProcess = text;

    while (textToProcess.length > 0) {
        if (textToProcess.length <= maxLength) {
            chunks.push(textToProcess);
            break;
        }

        let chunkCandidate = textToProcess.slice(0, maxLength);
        let splitIndex = maxLength;

        // Ưu tiên ngắt ở các vị trí tự nhiên để không làm hỏng định dạng
        const breakPoints = ['\n\n', '\n', '. ', ' '];
        for (const breakPoint of breakPoints) {
            const lastBreak = chunkCandidate.lastIndexOf(breakPoint);
            // Chỉ chấp nhận điểm ngắt nếu nó không quá ngắn
            if (lastBreak > maxLength * 0.7) {
                splitIndex = lastBreak;
                break;
            }
        }

        const chunk = textToProcess.slice(0, splitIndex);
        chunks.push(chunk);

        textToProcess = textToProcess.slice(splitIndex).trim();
    }

    return chunks.filter(chunk => chunk.trim().length > 0);
}


// --- LOGIC CHÍNH ---
const allChunks = [];

// TRƯỜNG HỢP 1: Xử lý file bài tập đặc biệt của bạn
// Nếu input bắt đầu bằng '%%%========', coi toàn bộ là một khối mã.
// Phần này đã xử lý đúng bằng cách dùng formatAsCode nên không cần thay đổi.
if (rawInput.trim().startsWith('%%%========')) {
    const codeChunks = getTextChunks(rawInput);
    for (const chunk of codeChunks) {
        allChunks.push({ json: { output: formatAsCode(chunk) } });
    }
}
// TRƯỜNG HỢP 2: Xử lý các loại văn bản hỗn hợp khác
else {
    const patterns = [
      '%%%.*?%%%[\\s\\S]*?\\begin{bt}[\\s\\S]*?\\end{bt}',
      '%%%.*?%%%[\\s\\S]*?\\begin{ex}[\\s\\S]*?\\end{ex}',
      '%%%.*?%%%[\\s\\S]*?\\begin{vd}[\\s\\S]*?\\end{vd}',
      '```[\\s\\S]*?```',
      '%%%.*?%%%',
      '\\begin{ex}[\\s\\S]*?\\end{ex}',
      '\\begin{bt}[\\s\\S]*?\\end{bt}',
      '\\begin{vd}[\\s\\S]*?\\end{vd}',
      '\\begin{lstlisting}[\\s\\S]*?\\end{lstlisting}',
      '\\begin{verbatim}[\\s\\S]*?\\end{verbatim}',
      '\\begin{code}[\\s\\S]*?\\end{code}',
      '\\$\\$[\\s\\S]*?\\$\\$',
      '<code>[\\s\\S]*?</code>',
      '<pre>[\\s\\S]*?</pre>',
      '`[^`\\n]+`',
    ];

    const specialBlocksRegex = new RegExp(`(${patterns.join('|')})`, 'g');
    let currentIndex = 0;
    let match;

    try {
        while ((match = specialBlocksRegex.exec(rawInput)) !== null) {
            // Xử lý phần văn bản thường (plain text) nằm TRƯỚC khối đặc biệt
            const plainText = rawInput.substring(currentIndex, match.index);
            if (plainText && plainText.trim()) {
                // *** THAY ĐỔI QUAN TRỌNG ***
                // Áp dụng escapeHtml cho văn bản thường để tránh lỗi
                const escapedText = escapeHtml(plainText);
                const plainTextChunks = getTextChunks(escapedText);
                for (const chunk of plainTextChunks) {
                    allChunks.push({ json: { output: chunk } });
                }
            }

            // Xử lý khối đặc biệt (đã được xử lý đúng từ trước)
            const specialBlock = match[0];
            if (specialBlock && specialBlock.trim()) {
                const specialBlockChunks = getTextChunks(specialBlock);
                for (const chunk of specialBlockChunks) {
                    // formatAsCode đã bao gồm escapeHtml bên trong
                    allChunks.push({ json: { output: formatAsCode(chunk) } });
                }
            }
            currentIndex = specialBlocksRegex.lastIndex;
        }

        // Xử lý phần văn bản thường còn lại ở CUỐI cùng
        const remainingText = rawInput.substring(currentIndex);
        if (remainingText && remainingText.trim()) {
            // *** THAY ĐỔI QUAN TRỌNG ***
            // Cũng áp dụng escapeHtml cho phần còn lại này
            const escapedText = escapeHtml(remainingText);
            const plainTextChunks = getTextChunks(escapedText);
            for (const chunk of plainTextChunks) {
                allChunks.push({ json: { output: chunk } });
            }
        }
    } catch (error) {
        console.error('Lỗi khi xử lý với regex, chuyển sang phương án dự phòng:', error);
        // Phương án dự phòng: escape toàn bộ nội dung nếu regex lỗi
        const escapedText = escapeHtml(rawInput);
        const fallbackChunks = getTextChunks(escapedText);
        for (const chunk of fallbackChunks) {
            allChunks.push({ json: { output: chunk } });
        }
    }
}

// Trường hợp dự phòng cuối cùng nếu không có chunk nào được tạo
if (allChunks.length === 0 && rawInput.trim()) {
    const escapedText = escapeHtml(rawInput);
    const fallbackChunks = getTextChunks(escapedText);
    for (const chunk of fallbackChunks) {
        allChunks.push({ json: { output: chunk } });
    }
}

return allChunks;

