// Lấy tất cả các mục đầu vào từ node trước
const items = $input.all();

// Regex 1: Tìm mọi loại link YouTube trong văn bản.
const youtubeLinkRegex = /(https?:\/\/(?:www\.)?(?:youtu\.be\/|youtube\.com\/(?:watch\?v=|embed\/|shorts\/))[\w\-=&?%._]+)/i;

// Regex 2: Trích xuất ID video từ một link YouTube.
// Nó tìm kiếm các chuỗi như v=, shorts/, embed/, .be/ và lấy chuỗi ký tự (ID) theo sau.
const videoIdRegex = /(?:v=|shorts\/|embed\/|\.be\/)([a-zA-Z0-9_-]{11})/;

// Duyệt qua từng mục đầu vào để xử lý riêng lẻ
for (const item of items) {
  // QUAN TRỌNG: Lấy văn bản từ trường của MỤC HIỆN TẠI.
  // Thay 'myText' bằng tên trường chứa văn bản của bạn (ví dụ: 'message', 'text', 'content'...).
  const inputText = $('Switch').first().json.message.text; 

  if (inputText) {
    // Sử dụng Regex 1 để tìm link trong văn bản
    const linkMatch = inputText.match(youtubeLinkRegex);

    // Nếu tìm thấy link
    if (linkMatch && linkMatch[0]) {
      const extractedLink = linkMatch[0];
      const cleanedText = inputText.replace(extractedLink, '').trim();
      let videoId = null; // Khởi tạo videoId là null

      // Sử dụng Regex 2 để tìm ID trong link vừa trích xuất
      const idMatch = extractedLink.match(videoIdRegex);

      // Nếu tìm thấy ID (nằm ở nhómจับที่ 1)
      if (idMatch && idMatch[1]) {
        videoId = idMatch[1];
      }

      // Gán cả 3 kết quả vào mục hiện tại
      item.json.extractedLink = extractedLink;
      item.json.cleanedText = cleanedText;
      item.json.videoId = videoId;

    } else {
      // Nếu không tìm thấy link, gán các giá trị mặc định
      item.json.extractedLink = null;
      item.json.cleanedText = inputText; // Giữ nguyên văn bản gốc
      item.json.videoId = null;
    }
  } else {
      // Xử lý trường hợp không có inputText
      item.json.extractedLink = null;
      item.json.cleanedText = null;
      item.json.videoId = null;
  }
}

// Trả về các mục đã được xử lý
return items;

/**
 * Hàm chuyển đổi thời lượng từ định dạng ISO 8601 (VD: "PT1M30S") sang giây.
 * Định dạng ISO 8601 cho thời lượng bắt đầu bằng 'P', theo sau là 'T' cho
 * các thành phần thời gian (Hours, Minutes, Seconds).
 * @param {string} isoDuration - Chuỗi thời lượng ISO 8601.
 * @returns {number|null} - Tổng số giây, hoặc null nếu định dạng không hợp lệ.
 */
function parseIsoDurationToSeconds(isoDuration) {
  if (!isoDuration || !isoDuration.startsWith('PT')) {
    return null;
  }

  // Regex để trích xuất số giờ (H), phút (M), giây (S)
  const regex = /PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/;
  const matches = isoDuration.match(regex);

  if (!matches) {
    return null;
  }

  // parseInt(matches[index] || 0) sẽ chuyển đổi chuỗi số thành số nguyên.
  // Nếu một phần (H, M, hoặc S) không tồn tại, '|| 0' sẽ đảm bảo nó có giá trị là 0.
  const hours = parseInt(matches[1] || 0);
  const minutes = parseInt(matches[2] || 0);
  const seconds = parseInt(matches[3] || 0);

  return (hours * 3600) + (minutes * 60) + seconds;
}


// Lấy tất cả các mục đầu vào từ node trước
const items = $input.all();

// Vòng lặp qua từng mục để xử lý
for (const item of items) {
  // Giả sử biến duration của bạn nằm trong `item.json.duration`
  // Hãy thay đổi `duration` thành tên trường thực tế của bạn nếu cần
  const isoDuration = $input.first().json.contentDetails.duration;

  // Chuyển đổi chuỗi ISO thành tổng số giây
  const totalSeconds = parseIsoDurationToSeconds(isoDuration);

  // Nếu không có thời lượng hoặc chuyển đổi thất bại, bỏ qua mục này
  if (totalSeconds === null || totalSeconds < 0) {
      item.json.totalSeconds = null;
      item.json.timeSegments = []; // Trả về mảng rỗng
      continue; // Chuyển sang mục tiếp theo
  }

  // Gán tổng số giây vào kết quả để dễ kiểm tra
  item.json.totalSeconds = totalSeconds;

  const timeSegments = [];
  const chunkDuration = 600; // Thời lượng mỗi đoạn là 600 giây

  // Nếu video có thời lượng, bắt đầu chia đoạn
  if (totalSeconds > 0) {
      // Vòng lặp bắt đầu từ 0, mỗi lần tăng lên 600 giây
      for (let startTime = 0; startTime < totalSeconds; startTime += chunkDuration) {

          // Tính thời gian kết thúc của đoạn hiện tại
          // Dùng Math.min để đảm bảo đoạn cuối cùng không vượt quá tổng thời lượng video
          const endTime = Math.min(startTime + chunkDuration, totalSeconds);

          // Thêm đoạn {start, end} vào mảng kết quả
          timeSegments.push({
              start: startTime,
              end: endTime
          });
      }
  }

  // Gán mảng các đoạn thời gian vào mục hiện tại
  item.json.timeSegments = timeSegments;
}

// Trả về các mục đã được xử lý
return items;

// Lấy tất cả các mục đầu vào từ node trước
const items = $input.all();

// Vòng lặp qua từng mục để xử lý
for (const item of items) {
  // 1. Lấy mảng 'transcript' từ dữ liệu JSON của mục hiện tại
  const transcriptArray = item.json.transcript;

  // 2. Kiểm tra xem 'transcriptArray' có tồn tại và có phải là một mảng không
  //    Đây là một bước an toàn để tránh lỗi nếu dữ liệu đầu vào không như mong đợi
  if (transcriptArray && Array.isArray(transcriptArray)) {

    // 3. Sử dụng phương thức .join() để nối tất cả các phần tử của mảng.
    //    '\n\n' được dùng làm ký tự phân tách. Nó sẽ tạo một dòng trống 
    //    giữa mỗi đoạn transcript, giúp văn bản cuối cùng dễ đọc hơn.
    const fullTranscript = transcriptArray.join('\n\n');

    // 4. Gán chuỗi văn bản đã nối vào một trường mới, ví dụ: 'fullTranscript'
    item.json.fullTranscript = fullTranscript;

    // (Tùy chọn) Bạn có thể xóa mảng 'transcript' gốc nếu không cần nữa
    // delete item.json.transcript;

  } else {
    // Nếu không có mảng 'transcript', gán một giá trị mặc định
    // để tránh lỗi ở các node sau
    item.json.fullTranscript = ''; 
  }
}

// Trả về các mục đã được xử lý với trường mới
return items;

